$(document).ready(function() {


	$(".owl-carousel").owlCarousel({
		loop:true,
	    margin:0,
	    nav:true,
	    items: 3,
	    // responsive:{
	    //     0:{
	    //         items:1
	    //     },
	    //     600:{
	    //         items:3
	    //     },
	    //     1000:{
	    //         items:5
	    //     }
	    // }
	});

	// $('.bxslider').bxSlider({
	//   mode: 'vertical',
	//   slideMargin: 5
	// });




	//Open menu
	$(".menu").click(function(){
		$(".menu-page").addClass('menu-page__active');
		$(".social-link").addClass('social-link--shadow');
		$(".pager").fadeOut(0);
		
	});

	//Close menu
	// if $(".menu-page").containsClass('menu-page__active') {
		$(".menu-page__close").click(function(){
			$(".menu-page").removeClass('menu-page__active');
		$(".social-link").removeClass('social-link--shadow');
		$(".pager").fadeIn(0);
			
		});

	// }

	//плавный скролл

	$("#scroll-link").on("click", "a", function (event) {
		//отменяем стандартную обработку нажатия по ссылке
		event.preventDefault();

		//забираем идентификатор бока с атрибута href
		var id  = $(this).attr('href'),

		//узнаем высоту от начала страницы до блока на который ссылается якорь
			top = $(id).offset().top;
		
		//анимируем переход на расстояние - top за 1500 мс
		$('#inner-page').animate({scrollTop: top}, 1000);
	});


	var myMap;

	// Дождёмся загрузки API и готовности DOM.
	ymaps.ready(init);

	function init () {
	    // Создание экземпляра карты и его привязка к контейнеру с
	    // заданным id ("map").
	    myMap = new ymaps.Map('contact__map', {
	        // При инициализации карты обязательно нужно указать
	        // её центр и коэффициент масштабирования.
	        center: [52.34841878869259,4.941847999999977], 
	        zoom: 16,
	        controls: []
	    }, {
	        searchControlProvider: 'yandex#map'
	    });
	    var myPlacemark = new ymaps.Placemark([52.34841878869259,4.941847999999977], {},{
	    	iconLayout: 'default#image',
	        iconImageHref: 'img/map-marker.png',
	        iconImageSize: [30, 42]
	    });
		myMap.geoObjects.add(myPlacemark);
	    myMap.behaviors.disable('drag');
	}

	//Close popup by clicking on "cross" or "shadow"
	$(".page__wrap").click(function(){
		$(".modal").fadeOut(500);
	});
 	
	// Close popup by Esc
	$(this).keydown(function(eventObject){
        if (eventObject.which == 27)
            $('.modal').fadeOut(500);
    });

 
});